INSERT INTO ref_nnml_word_source(srcid, src_name, query, min_word_num, min_word_percent, max_word_percent) VALUES (1, 'src_scan_script_1', 'SELECT tmp_nnml_ip.ipid, src_scan_script.value, src_scan_script.script FROM tmp_nnml_ip INNER JOIN src_scan_ip ON tmp_nnml_ip.ip=src_scan_ip.ip INNER JOIN src_scan_port ON src_scan_ip.ipid=src_scan_port.ipid INNER JOIN src_scan_script ON src_scan_port.portid=src_scan_script.portid WHERE src_scan_script.script IN (''http-comments-displayer'');', 3, 2.0, 95.0);
INSERT INTO ref_nnml_word_source(srcid, src_name, query, min_word_num, min_word_percent, max_word_percent) VALUES (2, 'src_scan_script_2', 'SELECT tmp_nnml_ip.ipid, src_scan_script.value, src_scan_script.script FROM tmp_nnml_ip INNER JOIN src_scan_ip ON tmp_nnml_ip.ip=src_scan_ip.ip INNER JOIN src_scan_port ON src_scan_ip.ipid=src_scan_port.ipid INNER JOIN src_scan_script ON src_scan_port.portid=src_scan_script.portid WHERE src_scan_script.script IN (''http-server-header'', ''http-title'', ''ssl-cert'', ''ssh-auth-methods'', ''ssh-hostkey'', ''ssh2-enum-algos'', ''ntp-info'', ''snmp-info'', ''banner'', ''dns-service-discovery'', ''sip-methods'', ''rtsp-url-brute'', ''vnc-info'', ''ftp-anon'', ''ftp-syst'', ''rpcinfo'', ''snmp-processes'', ''afp-serverinfo'', ''modbus-discover'', ''ldap-rootdse'') AND value NOT IN (''Couldn''''t find any comments.'', ''Couldn''''t find any error pages.'', CHR(10), ''Site doesn''''t have a title (text/html).'', '''', ''Site doesn''''t have a title.'');', 3, 0, 95.0);
